import React, { Component } from 'react';
import Validator from './Validator';

class App extends Component {
  constructor() {
    super()

    this.state = {
      username: '',
      password: '',
      confirmPassword: '',
      valid: false 
    }
  }
handleChange = (e) => {
  this.setState({
    [e.target.name] : e.target.value
  })
}

handleValidate = (e) => {
  e.preventDefault()
  if (this.state.password === this.state.confirmPassword) { 
    this.setState({ 
      valid: true
    })
  } else { 
    this.setState({
      valid: false
    })
  }

}

  render() {
    return (
      <Validator handleChange={this.handleChange}/>
    );
  }
}

export default App;
